mapper.insert:
insert into message values(
	msg_seq.nextval, #{msgSender}, #{msgReceiver}, #{msgTitle}, #{msgContent}, 0, to_char(sysdate, 'yyyy-mm-dd')
)

mapper.update:
update fservice set fs_Title=#{fsTitle}, fs_content=#{fsContent},
fs_price=#{fsPrice}, fs_working=#{fsWorking},
fs_photo=#{fsPhoto} where fs_no = #{fsNo}
